import pandas as pd
import numpy as np
import os
import sys
import matplotlib.pyplot as plt

def retrieve_means(dir_path, ordinate):
	means = []
	filenames = next(os.walk(dir_path))[2]
	
	for filename in filenames:
		csv_file = pd.read_csv(dir_path+filename)
		csv_file.drop(csv_file.columns[[0,1,2,3]], axis=1, inplace=True)
		csv_file = csv_file.ix[4:]
		means = np.append(means, csv_file)
#		print(means)
	
	return means
	

def plot_lorenz_curve(dir_path, ordinate):
	means = retrieve_means(dir_path, ordinate)
	samples = np.sort(means)
	T_sample_sum = np.sum(samples)
	
	i=0
	partial_sum = []
	partial_sum.append(samples[i])
	i=i+1
	for j in range(len(samples)-1):
		partial_sum.append(samples[i]+partial_sum[i-1])
		i=i+1
	
	y_values = []
	for i in partial_sum:
		y_values.append(i/T_sample_sum)
	j=1
	x_values = []
	for i in range(len(samples)):
		x_values.append(j/len(samples))
		j=j+1
		
	np_x_values = np.array(x_values)	
	np_y_values = np.array(y_values)
	
